clc; clear all; close all#ROSETAea = -530.6;ta = deg2rad(0);eb = 327.2;tb = deg2rad(-60.8);ec = -122.9;tc = deg2rad(-121.6);def = [ea; eb; ec];t = [ta; tb; tc];A = [cos(t).^2 sin(t).^2 sin(t).*cos(t)];def_re = A\defE = [def_re(1) def_re(3)/2; def_re(3)/2 def_re(2)]def_prin = eig(E)max_def_cis = max(def_prin)- min(def_prin) #cuidado que é só o modulo, dependendo da orientação, o valor pdoe ser diferentemed = (E(1,1) + E(2,2))/2angulo_principal1 = rad2deg(atan(E(1,2)/(E(1,1)-med))/2)angulo_principal2 = 90 - angulo_principal1 shear = rad2deg(atan((E(1,1)-med)/E(1,2))/2)